(8, 'Upacara Virtual HUT RI ke 75', 'upacara-virtual-hut-ri-ke-75', 'SMA Islam PB Soedirman 2 Bekasi', '<p><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif;\">Assalamualaikum, WR.WB</span></p>\r\n\r\n<p><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif;\">Mengharapkan kehadiran Bapak ibu guru, karyawan, Siswa dan seluruh civitas akademik SMA Islam PB. Soedirman 2 Bekasi pada Upacara Virtual HUT RI ke 75 pada&nbsp; :</span></p>\r\n\r\n<p><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif;\">Hari : Senin, 17 Agustus 2020.</span></p>\r\n\r\n<p><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif;\">Acara :&nbsp; Jam : 08:45 - 09:00 pembukaan pada tautan : <a href=\"https://www.youtube.com/watch?v=8EaqMEp8YWs\">https://www.youtube.com/watch?v=8EaqMEp8YWs</a></span></p>\r\n\r\n<p><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Jam : 09:00 - 09:45 : Upacara Bendera HUT RI ke 75 pada tautan :&nbsp;</span><a href=\"https://youtu.be/XX1eRf5Juw8\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif;\">https://youtu.be/XX1eRf5Juw8</span></a></p>\r\n\r\n<p><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif;\">Demikian Undangan ini kami sampaikan agar di laksanakan dengan penuh tanggung jawab.</span></p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp;<span style=\"font-family:lucida sans unicode,lucida grande,sans-serif;\">Ttd</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif;\">Kepala Sekolah</span></p>\r\n', '17-08-2020', '06:00 AM'),
(10, 'Webinar Guru Digital', 'webinar-guru-digital', 'Microsoft Teams', '<p>â‡ï¸ Webinar Guru Digital&nbsp;</p>\r\n\r\n<p>Hari&nbsp; &nbsp;: Sabtu, 17 Oktober 2020</p>\r\n\r\n<p>Pukul : 9:30 - 11:30 WIB</p>\r\n\r\n<p>Via&nbsp; &nbsp; &nbsp;:&nbsp; Room Meeting Microsoft Teams (Kapasitas 200 peserta)&nbsp;</p>\r\n\r\n<p>Link Registrasi: http://bit.ly/Reg-Webinar-Pangsoed2&nbsp;</p>\r\n\r\n<p>Atau melalui live streaming Youtube : https://youtu.be/vLyxJoxl5Uk&nbsp;</p>\r\n\r\n<p>Info : wa.me/6281374626881</p>\r\n', '17-10-2020', '09:30 AM'),
(11, 'Pangfest 4.0 Pangsud Festifal', 'pangfest-4.0-pangsud-festifal', 'Daring', '<p>â‡ï¸ Pangfest 4.0 Pangsud Festifal</p>\r\n\r\n<p>Tanggal&nbsp; : 20-28 Oktober 2020 melalui Perlombaan Siswa Tingkat Nasional dalam bidang Agama, Olah raga, Seni dan Bahasa secara virtual.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Link Regstrasi &amp; Informasi:&nbsp; http://bit.ly/infopangfest&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Melalui Kecakapan Abad 21 C4 Pandemi Covid-19 Bukan Menjadi Penghalang Menuju Indonesia Maju..</p>\r\n', '20-10-2020', '08:30 AM'),
(12, 'â‡ï¸ Live Opening Milad ke 17', 'â‡ï¸-live-opening-milad-ke-17', 'https://youtu.be/nB9MEqOehFY', '<p>â‡ï¸ Live Opening Milad ke 17&nbsp;</p>\r\n\r\n<p>Hari&nbsp; &nbsp;: Jumat, 16 Oktober 2020</p>\r\n\r\n<p>Pukul :10:00 WIB&nbsp;</p>\r\n\r\n<p>Link&nbsp; &nbsp; : https://youtu.be/nB9MEqOehFY&nbsp;</p>\r\n', '16-10-2020', '10:00 AM'),
