INSERT INTO `ekstrakurikuler` (`id`, `category`, `title`, `tujuan`, `activity`, `jadwal`, `pic`, `keberhasilan`) VALUES
(1, 'wajib', 'Pramuka', 'Menumbuhkan kemandirian, kedisiplinan, kepemimpinan, serta jiwa gotong royong melalui pendidikan kepramukaan.', 'Latihan rutin materi SKU/SKK & baris-berbaris.\r\nPerkemahan (Perjusa/Perjusami) dan bakti masyarakat.\r\nLomba tingkat pramuka dan simulasi tanggap darurat.', 'Setiap Rabu; event sesuai kalender Sekolah', 'Pembina Pramuka', 'Kehadiran rata-rata ≥85%.\r\nKenaikan tingkat SKU minimal 1 level/tahun.'),
(2, 'wajib', 'Rohis', 'Membina keimanan, akhlak, dan pembiasaan ibadah serta kepedulian sosial keagamaan.', 'kajian pekanan & mentoring.\r\nPeringatan hari besar Islam (PHBI).\r\nBakti sosial dan program kepedulian.', 'Hari Jumat', 'Pembina Rohis dan Guru PAI', 'Keterlibatan lintas kelas meningkat.\r\nProgram sosial keagamaan periodik terselenggara.\r\n        ],'),
(3, 'wajib', 'Keputrian', 'Penguatan karakter dan keterampilan siswi melalui pembinaan adab, kesehatan remaja, dan kepemimpinan.', 'Kelas adab & etika pergaulan.\r\nEdukasi kesehatan remaja & manajemen diri.\r\nPelatihan keterampilan praktis (presentasi, organisasi).', 'Setiap Jumat', 'Pembina Keputrian & Guru PAI', 'Kehadiran konsisten ≥85%.\r\nPeningkatan perilaku positif di kelas & kegiatan.'),
(4, 'pilihan', 'PAMCO ( Pangsoed Mountain Climbing & Outbond)', 'Membentuk pribadi yang mandiri, disiplin, tangguh, dan memiliki kesadaran konservasi alam. Meningkatkan keterampilan bertahan hidup (survival) dan manajemen risiko di alam terbuka.', 'Latihan navigasi darat.\r\nPelatihan Dasar Mountaineering.          Pendakian Gunung Terprogram.          Program konservasi', '1 kali seminggu,', 'Pembina PAMCO & Pelatih', 'Lulus tes teknis navigasi darat dan tali temali.\r\nAnggota mampu membuat rencana perjalanan (trip pla'),
(5, 'pilihan', 'KIR ( Karya Ilmiah Remaja)', 'Melatih nalar ilmiah, riset sederhana, dan kemampuan presentasi karya.', 'Proyek riset mini & penulisan laporan.\r\nKunjungan laboratorium/industri.\r\nLomba karya tulis ilmiah/pameran sains.', '1x Perminggu', 'Pembina KIR& Guru Saint', 'Minimal 1 Karya persemester.\r\npartisipasi lomba KTI meingkat'),
(6, 'pilihan', 'Silat', 'Meningkatkan kebugaran, disiplin, dan prestasi bela diri pencak silat.', 'Latihan teknik jurus, fisik, dan sparring terukut.\r\nUjian kenaikan meningkat.\r\nKejujuran antar sekolah', '1x perminggu', 'Pelatih Siat & Pembina Sport Club', 'VO2max/kebugaran meningkat.\r\npencapaian kejuaraan sesuai target.'),
(7, 'pilihan', 'Futsal', 'Meningkatkan kebugaran, disiplin, dan prestasi dalam cabang olahraga futsal.', 'Mengembangkan kemampuan teknis individu (dribbling, passing, shooting) dalam ruang sempit.\r\nMeningkatkan taktik bermain tim yang cepat dan efektif.\r\nMenumbuhkan sportivitas, disiplin, dan semangat kompetisi untuk meraih prestasi.', '1x perminggu', 'Pelatih dan Pembina Sport Club', 'Peningkatan keterampilan individu.\r\npeningkatan kebugaran.\r\nHasil maksimal di setiap lomba.'),
(8, 'pilihan', 'Basket', 'Mengembangkan keterampilan dasar dan strategi bermain bola basket, meningkatkan kebugaran fisik dan kerja sama tim, serta meraih prestasi dalam kompetisi.', 'Latihan teknik Teknik dasar.\r\nLatihan fisik.\r\nSimulasi taktik dan strategi.', '1x perminggu', 'Pembina Sport Club dan Pelatih', 'Peningkatan keterampilan individu.\r\nPeningkatan Kebugaran.\r\nHasil maksimal di setiap perlombaan.'),
(9, 'pilihan', 'Tari Tradisional', 'Melestarikan budaya daerah, mengembangkan kreativitas, dan meningkatkan kepekaan rasa serta keterampilan gerak tari tradisional.', 'Latihan Teknik Dasar.\r\nMempelajari dan menghafal Koreografi tari daerah.\r\nLatihan Penghayatan dan Ekspresi.\r\nTampil di acara pentas seni/ Lomba antar sekolah.', '1x perminggu', 'Pembina Tari Daerah', 'Penguasaan gerakan tari daerah.\r\nAktif dalam pementasan sekolah dan lomba.'),
(10, 'OSIS', 'BESS ( Badan Eksekutif Siswa Soedirman )', 'Mewadahi kepemimpinan siswa, perencanaan program sekolah, dan penguatan budaya partisipasi.', 'Rapat kerja & penyusunan program tahunan.\r\nPelaksanaan event sekolah (HUT, class meeting, bakti sosial).\r\nPelatihan kepemimpinan (internal/kolaborasi).', 'Menyesuaikan Jadwal Event', 'Pembina BESS & Wakasek Kesiswaan', 'Realisasi program ≥80% sesuai timeline.\r\nKeterlibatan panitia lintas kelas meningkat.');
