(28, 'Alur Pendaftaran', 'alur-pendaftaran', 'Alur Pendaftaran', '<h2><span style=\"font-family:georgia,serif;\">Segera daftarkan Putra/Putri Anda di SMA Islam PB Soedirman 2 Bekasi melalui Pendaftaran Online:&nbsp;</span></h2>\r\n\r\n<h2><span style=\"font-family:georgia,serif;\">Klik link ini :&nbsp;<a href=\"http://bit.ly/ppdbpangsoed2\">http://bit.ly/ppdbpangsoed2</a></span><br />\r\n&nbsp;</h2>\r\n\r\n<h3><span style=\"font-family:georgia,serif;\">Contact Person : WA&nbsp; 0821 2224 1232</span></h3>\r\n\r\n<p><span style=\"font-family:georgia,serif;\">Daftar Ulang kelas XI dan XII : Rp 600.000 belum termasuk uang kegiatan.</span></p>\r\n\r\n<hr />\r\n<p><img alt=\"\" border=\"0\" height=\"500\" hspace=\"0\" src=\"/images/images/13(3).png\" style=\"width:1280px;height:500px;margin-top:0px;margin-bottom:0px;margin-left:0px;margin-right:0px;border:0px solid black;\" vspace=\"0\" width=\"1280\" /></p>\r\n\r\n<hr />\r\n<p><img alt=\"\" border=\"0\" height=\"500\" hspace=\"0\" src=\"/images/images/14(4).png\" style=\"width:1280px;height:500px;margin-top:0px;margin-bottom:0px;margin-left:0px;margin-right:0px;border:0px solid black;\" vspace=\"0\" width=\"1280\" /></p>\r\n\r\n<hr />\r\n<p><img alt=\"\" border=\"0\" height=\"500\" hspace=\"0\" src=\"/images/images/15(2).png\" style=\"width:1280px;height:500px;margin-top:0px;margin-bottom:0px;margin-left:0px;margin-right:0px;border:0px solid black;\" vspace=\"0\" width=\"1280\" /></p>\r\n', '06-01-2020', '10:05 AM'),
(29, 'Fasilitas Sekolah', 'fasilitas-sekolah', 'Fasilitas Sekolah', '<h1><img alt=\"\" src=\"/images/images/11.png\" style=\"width: 100%; height: 100%;\" /></h1>\r\n\r\n<h1><span style=\"font-size:14px;\"><span style=\"font-family:comic sans ms,cursive;\"><strong>Fasilitas Sekolah SMAI PB. Soedirman 2 Bekasi</strong></span></span></h1>\r\n\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:comic sans ms,cursive;\"><strong><img alt=\"\" src=\"/images/images/WhatsApp%20Image%202020-11-06%20at%209_01_25%20AM.jpeg\" style=\"width: 100%; height: 100%;\" /></strong></span></span></p>\r\n\r\n<p><img alt=\"\" src=\"/images/images/lapangan%20baru.png\" style=\"width: 900px; height: 1200px;\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/images/images/kolam%20sekolah.png\" style=\"width: 941px; height: 705px;\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/images/images/LAP.jpeg\" style=\"width: 80%; height: 80%;\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n', '13-12-2017', '09:42 AM'),
