(53, 'Informasi MPLS~PAIS', 'informasi-mplspais', 'MPLS Kurikulum Merdeka', '<p>&nbsp;</p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0cm;margin-bottom:.0001pt;text-align:\r\njustify;line-height:normal\"><img alt=\"\" src=\"/images/images/Cover%20MPLS%202022.png\" style=\"width: 848px; height: 1200px;\" /></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0cm;margin-bottom:.0001pt;text-align:\r\njustify;line-height:normal\"><span style=\"mso-no-proof:yes\">Assalamualaikum Wr Wb<o:p></o:p></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0cm;margin-bottom:.0001pt;text-align:\r\njustify;line-height:normal\"><span style=\"mso-no-proof:yes\">Selamat bergabung di SMA Islam PB Soedirman 2 Bekasi tahun ajaran 2022 &ndash; 2023. Selama kegiatan MPLS PAIS berlangsung pada tanggal 20 hingga 22 Juli 2022, informasi kegiatan akan tersedia di halaman web sekolah. Sehingga adik-adik dapat melihat update kegiatan setiap saat secara digital di website ini.<o:p></o:p></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0cm;margin-bottom:.0001pt;text-align:\r\njustify;line-height:normal\"><br />\r\n<span style=\"mso-no-proof:yes\">Adapun kegiatan terdiri dari :<o:p></o:p></span></p>\r\n\r\n<ul>\r\n	<li class=\"MsoNormal\" style=\"margin-bottom: 0.0001pt; text-align: justify; line-height: normal;\"><span style=\"mso-no-proof:yes\">Senin, 18 Juli 2022 : Hari Pertama Masuk Sekolah<o:p></o:p></span></li>\r\n	<li class=\"MsoNormal\" style=\"margin-bottom: 0.0001pt; text-align: justify; line-height: normal;\"><span style=\"mso-no-proof:yes\">Selasa, 19 Juli 2022 : Tes Psikotest Online di rumah masing-masing dan persiapan MPLS<span style=\"mso-tab-count:1\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></li>\r\n	<li class=\"MsoNormal\" style=\"margin-bottom: 0.0001pt; text-align: justify; line-height: normal;\"><span style=\"mso-no-proof:yes\">Rabu, 20 Juli 2022<span style=\"mso-tab-count:1\">&nbsp;&nbsp;&nbsp;</span>: MPLS hari ke-1 (jam 06:45 &ndash; 16:00 WIB)<o:p></o:p></span></li>\r\n	<li class=\"MsoNormal\" style=\"margin-bottom: 0.0001pt; text-align: justify; line-height: normal;\"><span style=\"mso-no-proof:yes\">Kamis, 21 Juli 2022<span style=\"mso-tab-count:1\">&nbsp;&nbsp;</span>: MPLS hari ke-2 (jam 06:45 &ndash; 16:00 WIB)<o:p></o:p></span></li>\r\n	<li class=\"MsoNormal\" style=\"margin-bottom: 0.0001pt; text-align: justify; line-height: normal;\"><span style=\"mso-no-proof:yes\">Jumat, 22 Juli 2022<span style=\"mso-tab-count:1\">&nbsp;&nbsp;</span>: MPLS hari ke-3 (jam 06:45 &ndash; 16:00 WIB)<o:p></o:p></span></li>\r\n</ul>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0cm;margin-bottom:.0001pt;text-align:\r\njustify;line-height:normal\"><span style=\"mso-no-proof:yes\"><o:p>&nbsp;</o:p></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0cm;margin-bottom:.0001pt;text-align:\r\njustify;line-height:normal\"><b><span style=\"mso-no-proof:yes\">Contact Person MPLS :<o:p></o:p></span></b></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0cm;margin-bottom:.0001pt;text-align:\r\njustify;line-height:normal\"><b><span style=\"mso-no-proof:yes\">Kakak Nazwa : 0813-8087-2989<o:p></o:p></span></b></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0cm;margin-bottom:.0001pt;text-align:\r\njustify;line-height:normal\"><b><span style=\"mso-no-proof:yes\">Mrs Yeyen : 0877-5262-5925<o:p></o:p></span></b></p>\r\n\r\n<p>&nbsp;</p>\r\n', '16-07-2022', '11:18 AM'),
