(55, 'Seragam MPLS~PAIS', 'seragam-mplspais', 'Seragam MPLS~PAIS', '<p>&nbsp;</p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0cm;margin-bottom:.0001pt;text-align:\r\njustify;line-height:normal\"><b><span lang=\"EN-ID\" style=\"font-size:12.0pt;\r\nfont-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">SERAGAM<o:p></o:p></span></b></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0cm;margin-bottom:.0001pt;text-align:\r\njustify;line-height:normal\"><span lang=\"EN-ID\" style=\"font-size:12.0pt;\r\nfont-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Assalamualaikum Wr Wb<o:p></o:p></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0cm;margin-bottom:.0001pt;text-align:\r\njustify;line-height:normal\"><span lang=\"EN-ID\" style=\"font-size:12.0pt;\r\nfont-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Halo adik-adik peserta MPLS PAIS 2022. Kami akan melampirkan jadwal seragam yang harus kalian gunakan selama pra MPLS dan masa MPLS PAIS 2022 mulai dari hari Senin,18 Juli 2022 sampai hari Jumat,22 Juli 2022. Perhatikan ya jadwalnya supaya kalian tidak salah kostum.<o:p></o:p></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0cm;margin-bottom:.0001pt;text-align:\r\njustify;line-height:normal\"><span lang=\"EN-ID\" style=\"font-size:12.0pt;\r\nfont-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Senin: seragam putih biru (kerudung bergo putih untuk akhwat)<o:p></o:p></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0cm;margin-bottom:.0001pt;text-align:\r\njustify;line-height:normal\"><span lang=\"EN-ID\" style=\"font-size:12.0pt;\r\nfont-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Rabu: seragam putih biru (kerudung bergo putih untuk akhwat) <o:p></o:p></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0cm;margin-bottom:.0001pt;text-align:\r\njustify;line-height:normal\"><span lang=\"EN-ID\" style=\"font-size:12.0pt;\r\nfont-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Kamis: batik + bawa baju olahraga (kerudung bergo bebas untuk akhwat) <o:p></o:p></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0cm;margin-bottom:.0001pt;text-align:\r\njustify;line-height:normal\"><span lang=\"EN-ID\" style=\"font-size:12.0pt;\r\nfont-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">jumat: baju muslim biru (kerudung bergo putih untuk akhwat dan membawa peci untuk ikhwan)<o:p></o:p></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '18-07-2022', '04:01 PM'),
(56, 'MPLS~PAIS Hari ke 1', 'mplspais-hari-ke-1', 'MPLS~PAIS Hari ke 1', '<p><strong>Assalamualaikum Wr Wb</strong><br />\r\nHalo adik-adik Kelas 10 yang dirahmati ALLAH SWT. Yuk kita simak kegiatan MPLS~PAIS hari pertama di bawah ini :<br />\r\n<br />\r\n<span style=\"background-color:#FFFF00;\">Rabu, 20 Juli 2022</span></p>\r\n\r\n<p>06.45 &ndash; 07.00<span style=\"white-space:pre\"> </span>Siswa berkumpul di sekolah</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span style=\"white-space:pre\"> </span>Tadarus Al-Qur&rsquo;an</p>\r\n\r\n<p>07.00 &ndash; 07.10 <span style=\"white-space:pre\"> </span>Pembukaan MPLS&nbsp;</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span style=\"white-space:pre\"> </span>Pembacaan Al-Quran</p>\r\n\r\n<p>07.10 &ndash; 07.20 <span style=\"white-space:pre\"> </span>Sambutan Ketua Pelaksana MPLS</p>\r\n\r\n<p>07.20 &ndash; 07.40<span style=\"white-space:pre\"> </span>Sambutan Kepala Sekolah</p>\r\n\r\n<p>07.40 &ndash; 08.00 <span style=\"white-space:pre\"> </span>Sambutan Ketua Yayasan Masjid Pb. Soedirman sekaligus Pembukaan MPLS</p>\r\n\r\n<p>08.00 &ndash; 08.30<span style=\"white-space:pre\"> </span>Shalat Duha berjamaah</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span style=\"white-space:pre\"> </span>Hafalan Surat dan Kultum</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span style=\"white-space:pre\"> </span>Pemeriksaan tugas kelompok</p>\r\n\r\n<p>08.30 &ndash; 09.00 <span style=\"white-space:pre\"> </span>Makan Bersama dan Istirahat</p>\r\n\r\n<p>09.00 &ndash; 09.30<span style=\"white-space:pre\"> </span>Materi I : Tata Tertib dan Tata Krama Siswa (Mr. Rinaldo, S.Pd)</p>\r\n\r\n<p>09.30 &ndash; 11.30<span style=\"white-space:pre\"> </span>Tugas Kelompok Melukis Pot Bunga</p>\r\n\r\n<p>11.30 &ndash; 13.00<span style=\"white-space:pre\"> </span>ISHOMA</p>\r\n\r\n<p>13.00 &ndash; 13.30<span style=\"white-space:pre\"> </span>Materi II : Pengenalan Kurikulum Merdeka (Mr Wagino, S.Si, M.Pd)</p>\r\n\r\n<p>13.30 &ndash; 15.00<span style=\"white-space:pre\"> </span>Pengenalan Lingkungan Sekolah</p>\r\n\r\n<p>15.00 &ndash; 16.00<span style=\"white-space:pre\"> </span>Sholat Ashar</p>\r\n\r\n<p>16.00&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Pulang</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Contact Person MPLS :</strong></p>\r\n\r\n<p>Kakak Nazwa : 0813-8087-2989</p>\r\n\r\n<p>Mrs Yeyen : 0877-5262-5925</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div><b><span lang=\"EN-ID\" style=\"font-size:12.0pt;\r\nline-height:107%;font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-fareast-font-family:\r\nCalibri;mso-fareast-theme-font:minor-latin;mso-ansi-language:EN-ID;mso-fareast-language:\r\nEN-US;mso-bidi-language:AR-SA\"><span style=\"background-color:#FFFF00;\">BARANG BAWAAN</span></span></b></div>\r\n\r\n<div>\r\n<ol>\r\n	<li class=\"MsoNormal\" style=\"margin: 0cm 0cm 0.0001pt 18pt; text-align: justify; line-height: normal;\"><span lang=\"EN-ID\" style=\"font-size:12.0pt;font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Name Tag <o:p></o:p></span></li>\r\n	<li class=\"MsoNormal\" style=\"margin: 0cm 0cm 0.0001pt 18pt; text-align: justify; line-height: normal;\"><span lang=\"EN-ID\" style=\"font-size:12.0pt;font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Pot Dan Alat Lukis <o:p></o:p></span></li>\r\n	<li class=\"MsoNormal\" style=\"margin: 0cm 0cm 0.0001pt 18pt; text-align: justify; line-height: normal;\"><span lang=\"EN-ID\" style=\"font-size:12.0pt;font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Kalung Rempah Rempah <o:p></o:p></span></li>\r\n	<li class=\"MsoNormal\" style=\"margin: 0cm 0cm 0.0001pt 18pt; text-align: justify; line-height: normal;\"><span lang=\"EN-ID\" style=\"font-size:12.0pt;font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Snack Dodol Sapi Dan Minuman Bisa Tambah Jawa <o:p></o:p></span></li>\r\n	<li class=\"MsoNormal\" style=\"margin: 0cm 0cm 0.0001pt 18pt; text-align: justify; line-height: normal;\"><span lang=\"EN-ID\" style=\"font-size:12.0pt;font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Bekal Buat Makan Siang + Air Di Thumbler</span></li>\r\n	<li class=\"MsoNormal\" style=\"margin: 0cm 0cm 0.0001pt 18pt; text-align: justify; line-height: normal;\"><span lang=\"EN-ID\" style=\"font-size:12.0pt;font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><o:p></o:p></span><span style=\"font-family: &quot;Times New Roman&quot;, &quot;serif&quot;; font-size: 16px; font-style: italic;\">Balon Helium Satu Buah</span></li>\r\n	<li class=\"MsoNormal\" style=\"margin: 0cm 0cm 0.0001pt 18pt; text-align: justify; line-height: normal;\"><span lang=\"EN-ID\" style=\"font-size:12.0pt;font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Materi Kultum <o:p></o:p></span></li>\r\n	<li class=\"MsoNormal\" style=\"margin: 0cm 0cm 0.0001pt 18pt; text-align: justify; line-height: normal;\"><span lang=\"EN-ID\" style=\"font-size:12.0pt;font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Alat Tulis <o:p></o:p></span></li>\r\n	<li class=\"MsoNormal\" style=\"margin: 0cm 0cm 0.0001pt 18pt; text-align: justify; line-height: normal;\"><span lang=\"EN-ID\" style=\"font-size:12.0pt;font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Perlengkapan Sholat Dan Alquran <o:p></o:p></span></li>\r\n</ol>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-top:0cm;margin-right:0cm;margin-bottom:0cm;\r\nmargin-left:18.0pt;margin-bottom:.0001pt;text-align:justify;line-height:normal\"><span style=\"color:#FF0000;\"><b><span lang=\"EN-ID\" style=\"font-size:12.0pt;\r\nline-height:107%;font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-fareast-font-family:\r\nCalibri;mso-fareast-theme-font:minor-latin;mso-ansi-language:EN-ID;mso-fareast-language:\r\nEN-US;mso-bidi-language:AR-SA\">NOTE: PESERTA DILARANG MEMBAWA ALAT KOSMETIK, HANDPHONE DAN ALAT ELEKTRONIK LAINYA SERTA BARANG BARANG LAINYA YANG TIDAK DI PERLUKAN SELAMA MPLS PAIS 2022 BERLANGSUNG&nbsp;</span></b></span></p>\r\n</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div><strong><span style=\"background-color:#FFFF00;\">Tugas Pembuatan Twibbon</span></strong></div>\r\n\r\n<ol>\r\n	<li>Mulai posting di IG Pribadi&nbsp;hari Selasa, 19 Juli 2022&nbsp;</li>\r\n	<li>Batas Akhir posting di IG Pribadi hingga hari Rabu, 20 Juli 2022 Pukul 18:00 WIB</li>\r\n	<li>Follow IG Sekolah @smaipbsoedirman2bekasi dan IG BESS @besspangsoed2</li>\r\n	<li>Buat judul postingan yang kreatif dan positif</li>\r\n	<li>Tag akun IG Sekolah dan BESS</li>\r\n	<li>Berikan hastag : #pangsoed2bekasi #besspangsoed2 #mpls2022 #merdekabelajar #ppdb2023</li>\r\n	<li>Link Twibbon :&nbsp;<a href=\"http://twb.nz/pangsoed2bekasimpls2022\">http://twb.nz/pangsoed2bekasimpls2022</a></li>\r\n</ol>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div>&nbsp;</div>\r\n', '19-07-2022', '11:17 AM'),
(57, 'Mars Yasma PB Soedirman', 'mars-yasma-pb-soedirman', 'Mars Yasma PB Soedirman', '<p><em>Assalamualaikum Wr Wb</em></p>\r\n\r\n<p>Mari anak-anak kita pahami dan hafalkan Mars Yasma PB Soedirman<br />\r\nPerhatikan liriknya di bawah ini. Kamu juga bisa memutar videonya <a href=\"https://www.youtube.com/watch?v=G6ypEtcKwTU\"><span style=\"color:#008000;\"><em><strong>disini</strong></em></span></a></p>\r\n\r\n<p>tonton juga Mars Pelajar PB Soedirman <em><a href=\"https://www.youtube.com/watch?v=F8uN6eju7vQ&amp;t=33s\"><span style=\"color:#FF0000;\">disini</span></a></em></p>\r\n\r\n<p><br />\r\n<img alt=\"\" src=\"/images/images/Copy%20of%20Buku%20Panduan%20MPLS%202022.png\" style=\"width: 848px; height: 1200px;\" /></p>\r\n', '19-07-2022', '10:37 AM'),
(58, 'Materi PAIS', 'materi-pais', 'Pengenalan Al-Islam', '<p><img alt=\"\" src=\"/images/images/13(2).png\" style=\"width: 848px; height: 1200px;\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/images/images/14(3).png\" style=\"width: 848px; height: 1200px;\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/images/images/15(1).png\" style=\"width: 848px; height: 1200px;\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/images/images/16(2).png\" style=\"width: 848px; height: 1200px;\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/images/images/17(1).png\" style=\"width: 848px; height: 1200px;\" /><img alt=\"\" src=\"/images/images/18.png\" style=\"width: 848px; height: 1200px;\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/images/images/19.png\" style=\"width: 848px; height: 1200px;\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/images/images/20.png\" style=\"width: 848px; height: 1200px;\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/images/images/21.png\" style=\"width: 848px; height: 1200px;\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/images/images/22.png\" style=\"width: 848px; height: 1200px;\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/images/images/23.png\" style=\"width: 848px; height: 1200px;\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/images/images/24.png\" style=\"width: 848px; height: 1200px;\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/images/images/25.png\" style=\"width: 848px; height: 1200px;\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/images/images/26.png\" style=\"width: 848px; height: 1200px;\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/images/images/27.png\" style=\"width: 848px; height: 1200px;\" /></p>\r\n', '19-07-2022', '03:07 PM'),
(59, 'MPLS~PAIS Hari ke-2', 'mplspais-hari-ke-2', 'MPLS~PAIS Hari ke-2', '<p class=\"MsoNormal\" style=\"margin-bottom:0cm;margin-bottom:.0001pt;text-align:\r\njustify;line-height:normal\"><span lang=\"IN\" style=\"font-size:12.0pt;font-family:\r\n&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:IN\">Assalamualaikum Wr Wb<o:p></o:p></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0cm;margin-bottom:.0001pt;text-align:\r\njustify;line-height:normal\"><span lang=\"IN\" style=\"font-size:12.0pt;font-family:\r\n&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:IN\">Halo adik-adik peserta MPLS PAIS 2022. Selamat kalian sudah menjalani MPLS hari pertama. Persiapkan diri kalian untuk MPLS PAIS 2022 hari kedua ya....&nbsp; Hari kedua MPLS PAIS 2022 akan dilaksanakan pada hari Kamis, 21 Juli 2022 mulai dari pukul 06.45 sampai pukul 16.00 Berikut adalah susunan acara hari pertama MPLS PAIS 2022. Jangan lupa disimak ya....<o:p></o:p></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0cm;margin-bottom:.0001pt;text-align:\r\njustify;line-height:normal\"><b><span lang=\"IN\" style=\"font-size:12.0pt;\r\nfont-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:IN\">Kamis, 21 Juli 2022<o:p></o:p></span></b></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0cm;margin-bottom:.0001pt;text-align:\r\njustify;line-height:normal\"><span lang=\"IN\" style=\"font-size:12.0pt;font-family:\r\n&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:IN\">06.45 &ndash; 07.1</span><span style=\"font-size:12.0pt;font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">5&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span lang=\"IN\" style=\"font-size:12.0pt;font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:\r\nIN\">Siswa Berkumpul di Sekolah<o:p></o:p></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0cm;margin-bottom:.0001pt;text-align:\r\njustify;line-height:normal\"><span lang=\"IN\" style=\"font-size:12.0pt;font-family:\r\n&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:IN\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tadarus Al-Quran<o:p></o:p></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0cm;margin-bottom:.0001pt;text-align:\r\njustify;line-height:normal\"><span lang=\"IN\" style=\"font-size:12.0pt;font-family:\r\n&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:IN\">07.15 -07.3</span><span style=\"font-size:12.0pt;font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">0&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span lang=\"IN\" style=\"font-size:12.0pt;font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:\r\nIN\">Hafalan Surat dan Kultum<o:p></o:p></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0cm;margin-bottom:.0001pt;text-align:\r\njustify;line-height:normal\"><span lang=\"IN\" style=\"font-size:12.0pt;font-family:\r\n&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:IN\">07.30 &ndash; 08.00&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Shalat Dhuha<o:p></o:p></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0cm;margin-bottom:.0001pt;text-align:\r\njustify;line-height:normal\"><span lang=\"IN\" style=\"font-size:12.0pt;font-family:\r\n&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ansi-language:IN\">08.00 &ndash; 09.00&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Perkenalan ke Kelas-kelas<o:p></o:p></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0cm;margin-bottom:.0001pt;text-align:\r\njustify;line-height:normal\"><span lang=\"IN\" style=\"font-size:12.0pt;font-fam